(1, 2, 'raml', '#%RAML 0.8\r\ntitle: Amazon simple storage API\r\nversion: 1\r\nbaseUri: https://api.twitter.com/1.1\r\nprotocols: [HTTP, HTTPS]\r\nbaseUriParameters:\r\n  destinationBucket:\r\n    description: ''My big &quote;ol description.''\r\n    enum: [''test'']\r\nlimits:\r\n  level1:\r\n    day: 50\r\n    month: 1000\r\n  level2:\r\n    minute: 5\r\n    month: 250000\r\nresourceTypes:\r\n  - searchableCollection:\r\n      get:\r\n        queryParameters:\r\n          <<queryParamName>>:\r\n            description: Return <<resourcePathName>> that have their <<queryParamName>> matching the given value\r\n          <<fallbackParamName>>:\r\n            description: If no values match the value given for <<queryParamName>>, use <<fallbackParamName>> instead\r\nsecuritySchemes:\r\n  basic:\r\n    description: Use HTTP Basic Access Authentication for authentication.\r\n    type: Basic Authorization\r\n    describedBy:\r\n      headers:\r\n        Authorization:\r\n          description: The Authorization header as described in rfc1945 section 11.1\r\n          type: string\r\n  digest:\r\n    description: Use HTTP Digest Access Authentication for authentication.\r\n    type: Digest Authentication\r\n    describedBy:\r\n      headers:\r\n        Authorization:\r\n          description: The Authorization header as described in rfc2069 or rfc2617\r\n          type: string\r\n    settings:\r\n      realm: ''testrealm@host.com''\r\n      qop: ''auth''\r\n      opaque: ''5ccc069c403ebaf9f0171e9517f40e41''\r\n  custom:\r\n    description: Test merging with all methods\r\n    type: x-custom\r\n    describedBy:\r\n      headers:\r\n        Authorization:\r\n          description: Used to send a valid OAuth 2 access token. Do not use with the "access_token" query string parameter.\r\n          type: string\r\n          default: ''test''\r\n        Authorization2:\r\n          description: Used to send a valid OAuth 2 access token. Do not use with the "access_token" query string parameter.\r\n          type: string\r\n          default: ''test''\r\n      queryParameters:\r\n        access_token-cust:\r\n          description: Used to send a valid OAuth 2 access token. Do not use together with the "Authorization" header\r\n          type: string\r\n          default: ''test''\r\n      body:\r\n        application/json:\r\n          example: |\r\n            {"test":"here"}\r\n    settings:\r\n      method: post\r\n      url: https://api.twitter.com/oauth/request_token\r\n  oauth_2_0:\r\n    description: Dropbox supports OAuth 2.0 for authenticating all API requests.\r\n    type: OAuth 2.0\r\n    describedBy:\r\n      headers:\r\n        Authorization:\r\n          description: Used to send a valid OAuth 2 access token. Do not use with the "access_token" query string parameter.\r\n          type: string\r\n      queryParameters:\r\n        access_token:\r\n          description: Used to send a valid OAuth 2 access token. Do not use together with the "Authorization" header\r\n          type: string\r\n      body:\r\n        multipart/form-data:\r\n          formParameters:\r\n            key1:\r\n              description: Your API key\r\n              type: string\r\n      responses:\r\n        401:\r\n          description: Bad or expired token. This can happen if the user or Dropbox revoked or expired an access token. To fix, you should re-authenticate the user.\r\n        403:\r\n          description: Bad OAuth request (wrong consumer key, bad nonce, expired timestamp...). Unfortunately, re-authenticating the user won''t help here.\r\n    settings:\r\n      scopeType: space\r\n      scopes: [''https://www.googleapis.com/auth/plus.login'', ''https://www.googleapis.com/auth/plus.me'']\r\n      expiresType: none\r\n      tokenType: bearer\r\n      authorizeLocation: header\r\n      authorizationUri: https://accounts.google.com/o/oauth2/auth\r\n      accessTokenUri: https://accounts.google.com/o/oauth2/token\r\n      authorizationGrants: [ code ]\r\n  oauth_1_0a:\r\n    description: OAuth 1.0a continues to be supported for all API requests, but OAuth 2.0 is now preferred.\r\n    type: OAuth 1.0\r\n    settings:\r\n      legs: 3\r\n      callback: oob\r\n      signatureMethod: HMAC-SHA1\r\n      authorizeLocation: header\r\n      requestTokenMethod: POST\r\n      requestTokenUri: https://api.twitter.com/oauth/request_token\r\n      authorizationUri: https://api.twitter.com/oauth/authorize\r\n      tokenCredentialsMethod: POST\r\n      tokenCredentialsUri: https://api.twitter.com/oauth/access_token\r\n/{{static-calls}}:\r\n  /fakecall:\r\n    stability: 1\r\n    description: ''Define your static call here.''\r\n    /statuses/mentions_timeline.json:\r\n      get:\r\n        securedBy: [oauth_1_0a]\r\n/oauth/example/echo_api.php:\r\n  displayName: retrieve all users\r\n  baseUriParameters:\r\n    destinationBucket:\r\n      enum: [ "static" ]\r\n  get:\r\n    securedBy: [oauth_1_0a]\r\n    displayName: retrieve a user''s picture\r\n    queryParameters:\r\n      file:\r\n        description: ''My qdesk''\r\n        default: ''vacation.jpg''\r\n      size:\r\n        description: ''My qdesk2''\r\n        default: ''original''\r\n/:\r\n  post:\r\n    description: The POST operation adds an object to a specified bucket using HTML forms.\r\n    body:\r\n      application/x-www-form-urlencoded:\r\n        formParameters:\r\n          AWSAccessKeyId:\r\n            description: The AWS Access Key ID of the owner of the bucket who grants an Anonymous user access for a request that satisfies the set of constraints in the Policy.\r\n            type: string\r\n          acl:\r\n            description: Specifies an Amazon S3 access control list. If an invalid access control list is specified, an error is generated.\r\n            type: string\r\n          file:\r\n            - type: string\r\n              description: Text content. The text content must be the last field in the form.\r\n            - type: file\r\n              description: File to upload. The file must be the last field in the form.', 0, 0, NULL),
(2, 3, 'raml', '#%RAML 0.8\r\ntitle: Blockchain.info Simple Query API\r\nversion: 1\r\nbaseUri: https://blockchain.info/q\r\nprotocols: [HTTPS]\r\ntraits:\r\n  - secured:\r\n      queryParameters:\r\n        api_code:\r\n          description: Used to avoid API limits imposed by Blockchain.info\r\n          type: string\r\n          default: ''my-key''\r\n/{{static-calls}}:\r\n  /getdifficulty:\r\n    stability: 1\r\n    description: ''Get the Bitcoin blockchain difficulty.''\r\n    /getdifficulty:\r\n      get:\r\n        is: [secured]', 0, 0, NULL),
